AND Wmtl.source_line_id in
#{item}
AND Wmt.mc_task_id in
#{item}
AND Wmt.mc_task_no in
#{item}

select Wmt.mc_task_id from wip_mc_task Wmt join wip_mc_task_line Wmtl on Wmt.mc_task_id = Wmtl.mc_task_id where Wmtl.line_status = '110' and Wmt.mc_task_id in
#{item}
group by Wmt.mc_task_id
