mapper.insert:
insert into wip.wip_req_lines ( Line_Id , Header_Id , Organization_Id , Lot_Number , Pos_No , Item_No , Wkp_No , Unit_Qty , Req_Qty , Issued_Qty , Exceed_Reason , Original_Line_Id , Before_Line_Id , Line_Status , Change_Reason , Crt_User , Crt_Date , Upd_User , Upd_Date , Item_Id) SELECT Line_Id , Header_Id , Organization_Id , Lot_Number , Pos_No , Item_No , Wkp_No , Unit_Qty , Req_Qty , Issued_Qty , Exceed_Reason , Original_Line_Id , Before_Line_Id , Line_Status , Change_Reason , Crt_User , Crt_Date , Upd_User , Upd_Date , Item_Id FROM Wip.Wip_Req_Lines_Lack_Mv wrl WHERE 1 = 1
AND wrl.Header_Id IN
${wipEntityId}
AND wrl.Organization_Id IN
${organizationId}

mapper.select:
select Aa.organization_id as organizationId, Aa.header_id as moId, Aa.item_id as itemId, Aa.item_no as itemNo, Aa.wkp_no as wkpNo, max(Aa.unit_qty) as unitQty, sum(Aa.req_qty) as reqQty, sum(coalesce(Aa.issued_qty, 0)) as issuedQty, sum(req_qty) - sum(coalesce(Aa.issued_qty, 0)) as unIssuedQty, coalesce(1, 1) as componentYieldFactor /* TODO 同步BOM产出率 */ from ( select Wrl.organization_id, Wrl.header_id, Wrl.item_id, Wrl.item_no, Wrl.wkp_no, sum(Wrl.unit_qty) as unit_qty, sum(Wrl.req_qty) as req_qty, sum(coalesce(Wrl.issued_qty, 0)) as issued_qty from wip_req_lines Wrl where Wrl.line_status not in ('40', '50') and Wrl.organization_id = #{queryVO.organizationId}
and Wrl.item_id = #{queryVO.itemId}
and Wrl.header_id = #{queryVO.headerId}
and Wrl.wkp_no = #{queryVO.wkpNo}
and (Wrl.item_id in
#{itemKey}
or Wrl.item_no in
#{itemKey}
)
group by Wrl.organization_id, Wrl.header_id, Wrl.lot_number, Wrl.wkp_no, Wrl.item_id, Wrl.item_no ) Aa group by Aa.organization_id, Aa.header_id, Aa.wkp_no, Aa.item_id, Aa.item_no
