mapper.select:
select mrh.organization_id as organizationId,
mrh.mo_id as moId,
mrl.item_id as itemId,
mrl.wkp_no as wkpNo,
sum(coalesce(mrl.issued_qty, 0)) as unPostQty
from wip_mtr_rtc_l mrl
inner join wip_mtr_rtc_h mrh
on mrl.header_id = mrh.header_id
where mrh.mo_id = #{queryVO.moId}

    and mrl.wkp_no = #{queryVO.wkpNo}

and mrh.bill_type = #{queryVO.billType}

    and mrl.item_id = #{queryVO.itemId}


    and mrh.header_id  <>  #{queryVO.headerId}


    and (mrl.item_id in

        #{itemKey}

    or mrl.item_no in

        #{itemKey}

    )

and mrl.line_status in

    #{status}

group by mrh.organization_id, mrh.mo_id, mrl.item_id, mrl.wkp_no

select mrh.organization_id as organizationId,
mrh.factory_id as factoryId,
mrl.invp_no as invpNo,
mrl.item_id as itemId,
sum(coalesce(mrl.issued_qty, 0)) as unPostQty
from wip_mtr_rtc_l mrl
inner join wip_mtr_rtc_h mrh
on mrl.header_id = mrh.header_id
where mrh.organization_id = #{queryVO.organizationId}
and mrh.factory_id = #{queryVO.factoryId}

    and mrl.item_id = #{queryVO.itemId}

and mrh.bill_type = #{queryVO.billType}

    and mrh.header_id  <>  #{queryVO.headerId}


    and (mrl.item_id in

        #{itemKey}

    or mrl.item_no in

        #{itemKey}

    )

and mrl.line_status in

    #{status}

group by mrh.organization_id, mrh.factory_id, mrl.invp_no, mrl.item_id

select mrh.organization_id as organizationId, mrh.factory_id as factoryId, mrl.item_id as itemId, mra.invp_no as invpNo, mra.mtr_lot_no as lotNumber, sum(coalesce(mra.issued_qty, 0)) as unPostQty from wip_mtr_rtc_assign mra inner join wip_mtr_rtc_l mrl on mra.line_id = mrl.line_id inner join wip_mtr_rtc_h mrh on mrl.header_id = mrh.header_id where mrh.organization_id = #{queryVO.organizationId} and mrh.factory_id = #{queryVO.factoryId} and mrl.item_id = #{queryVO.itemId} and mra.assign_status = '110' and mrh.bill_type = #{queryVO.billType}
and mra.invp_no = #{queryVO.invpNo}
and mra.mtr_lot_no in
#{mtrLotNo}
and mrl.line_id <> #{queryVO.lineId}
and mrl.line_status in
#{status}
group by mrh.organization_id, mrh.factory_id, mrl.item_id, mra.invp_no, mra.mtr_lot_no
