mapper.select:
select siq.organization_id,
       siq.factory_id,
       siq.factory_no,
       siq.inventory_item_id,
       siq.subinventory_code,
       siq.lot_number,
       sum(siq.supply_qty) as supply_qty
from wip_mtr_sub_inv_qty_v siq
where siq.organization_id = #{subInvVOList[0].organizationId}
  and siq.factory_id = #{subInvVOList[0].factoryId}
  and (

      (siq.inventory_item_id = #{subInv.inventoryItemId}

          and siq.subinventory_code = #{subInv.subinventoryCode}


          and siq.lot_number = #{subInv.lotNumber}

      )

  )
group by siq.factory_no, siq.factory_id, siq.inventory_item_id, siq.organization_id,
         siq.subinventory_code, siq.lot_number

select wrh.organization_id , wrh.factory_id , siq.factory_no , siq.subinventory_code , mi.inventory_item_id , rli.mtr_lot_no as lot_number , siq.first_stockin_date , siq.supply_qty , rli.issued_qty as item_qty , (rli.issued_qty - coalesce(rli.unissued_qty, rli.issued_qty)) as item_issued_qty from wip_req_lot_issued rli inner join scm.md_item mi on rli.item_no = mi.item_code inner join wip_req_header wrh on rli.header_id = wrh.header_id left join wip_mtr_sub_inv_qty_v siq on wrh.organization_id = siq.organization_id and wrh.factory_id = siq.factory_id and rli.mtr_lot_no = siq.lot_number and mi.inventory_item_id = siq.inventory_item_id
and siq.subinventory_code = #{subinventoryCode}
where wrh.organization_id = #{organizationId} and wrh.factory_id = #{factoryId} and mi.inventory_item_id = #{itemId}
