mapper.select:
select siq.organization_id,
       siq.factory_id,
       siq.factory_no,
       siq.inventory_item_id,
       siq.subinventory_code,
       siq.lot_number,
       sum(siq.supply_qty) as supply_qty
from wip_mtr_sub_inv_qty_v siq
where siq.organization_id = #{subInvVOList[0].organizationId}
  and (

      (siq.inventory_item_id = #{subInv.inventoryItemId}

          and siq.factory_id = #{subInv.factoryId}


          and siq.subinventory_code = #{subInv.subinventoryCode}


          and siq.lot_number = #{subInv.lotNumber}

      )

  )
group by siq.factory_no, siq.factory_id, siq.inventory_item_id, siq.organization_id,
         siq.subinventory_code, siq.lot_number

select wrh.organization_id , wrh.factory_id , siq.factory_no , siq.subinventory_code , siq.inventory_item_id , siq.lot_number as lot_number , siq.first_stockin_date , siq.supply_qty , coalesce(rli.assign_qty, 0) as item_qty , coalesce(rli.issued_qty, 0) as item_issued_qty , case when(rli.id is null) then 'N' else 'Y' end as lot_assigned_flag /* 是否分配批次标识 */ from wip_mtr_sub_inv_qty_v siq inner join scm.md_item mi on siq.inventory_item_id = mi.inventory_item_id inner join wip_req_header wrh on siq.organization_id = wrh.organization_id and siq.factory_id = wrh.factory_id left join wip_req_lot_issued rli on siq.lot_number = rli.mtr_lot_no and wrh.header_id = rli.header_id and rli.status = '110' where siq.organization_id = #{organizationId} and siq.factory_id = #{factoryId} and siq.inventory_item_id = #{itemId}
and siq.subinventory_code = #{subinventoryCode}
and wrh.header_id = #{moId}
